void mainImage(out vec4 c, vec2 u) {
    c.xyz = iResolution;
    u = (u+u-c.xy)/c.x*2.;
    float t=iTime, x=u.x-t, y=u.y*1.2+sin(x)*.5;
    x = (fract(x + y * -cos(x)*.3 - t)+mod(ceil(t*9.), 6.))*.156;
    c = texture(iChannel0, vec2(x,y+.5));
    c = mix(vec4(.2, .3, .4, 1)+step(sin(ceil(u.x+t)*6e4), u.y)*.1, c, c.a);
}
