// Integer voxel traversal

// From Graphics Gems IV 
// "Voxel Traversal along a 3D Line" ðŸ‘€
// https://webdocs.cs.ualberta.ca/~graphics/books/GraphicsGems/gemsiv/vox_traverse.c
#define RES 128.0

//===================================================================================
float visitVoxel (vec2 uv, ivec2 pos)
{
	ivec2 pix = ivec2(uv * RES);
	return float((pix == pos));
}

//===================================================================================
float Line2D(vec2 uv, ivec2 pos, ivec2 dir)  
{  
	float c = 0.0;
	ivec2 s, a, b;

    s.x = sign(dir.x); dir.x = dir.x * s.x;
    s.y = sign(dir.y); dir.y = dir.y * s.y;

	b = dir + dir;
	int e   = dir.y - dir.x;
    int len = (dir.x + dir.y);
	// This is the traversal...
    for (int i = 0; i < len; i++)
	{
		c += visitVoxel(uv, pos);
		if (e < 0)
		{  
			pos.x += s.x;  
			e += b.y;
		}  
		else
		{  
			pos.y += s.y;  
			e -= b.x;
		}  
    } 
	return c;
} 


//===================================================================================
// This is the 3D voxel version...
float Line3D(vec2 uv, ivec3 pos, ivec3 dir)  
{  
    float c = 0.0;
	ivec3 s, a, b;

    s.x = sign(dir.x); a.x = dir.x * s.x;
    s.y = sign(dir.y); a.y = dir.y * s.y;
    s.z = sign(dir.z); a.z = dir.z * s.z;

	b = a + a;
	ivec3 e  = ivec3(a.y-a.x,  a.z-a.x, a.y-a.z);

    int len = (a.x + a.y);
	// This is the traversal...
    for (int i = 0; i < len; i++)
	{
		c += visitVoxel(uv, pos.xy);  
		if ( e.x < 0 )
		{  
			if ( e.y < 0 )
			{  
				pos.x += s.x;  
				e.x += b.y;
				e.y += b.z;  
			}  
			else
			{  
				pos.z += s.z;  
				e.y -= b.x;
				e.z += b.y;  
			}  
		}  
		else
		{  
			if ( e.z < 0 )
			{  
				pos.z += s.z;  
				e.y -= b.x;
				e.z += b.y;  
			}  
			else
			{  
				pos.y += s.y;  
				e.x -= b.x;
				e.z -= b.z;  
            }  
		}  
    } 
	return c;
} 

//===================================================================================
void mainImage( out vec4 colour, in vec2 coord )
{
    coord.xy+=.01;
	vec2 uv = coord.xy / iResolution.x;
	vec2 gr = coord.xy;
	float aspect = iResolution.y / iResolution.x;
	vec2 pixel = vec2(1.0);
	pixel.y *= aspect;
	gr.y    *= aspect;


	vec2 gridXY  = step(pixel,  mod(gr, iResolution.xy/RES)) * .75;
	vec3 backCol = vec3(gridXY.x*gridXY.y) * vec3(.5)+.15;

	float ang = iTime*2.;

	ivec2 beg = ivec2(RES*.5, RES*iResolution.y/iResolution.x *.5);
    float len = RES*.25;
    // Spin a line...
	ivec2 dxy = ivec2(floor(vec2(sin(ang)*len, cos(ang)*len)));
    // ...dxy includes length of ray

	float col = Line2D(uv, beg, dxy);
//    float col = Line3D(uv, ivec3(beg.x, beg.y, 0), ivec3(dxy.x, dxy.y, 0));

	colour = vec4(min(backCol+backCol*col*2.0, 1.0), 1.0);
}
