void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
}

vec2 pianoSingle(float time,float note)
{
    note += 15.;
    if (time<0.0)
        return vec2(0.0);

    float sampleX = 0.;

    float mult = pow(2.0,note/12.0);
    float f = 25.*55.*mult;
    for (int i=0; i<25; i++)
    {
        sampleX = (sampleX*1.5+sin(float(i)*2.+6.2831*f*time)*exp((-1.5-sqrt(f)/25.)*time))/2.5;
        f -= 55.*mult;
    }

    float balanceLeft = 0.75-note/12.0;
    float balanceRight = 1.0-balanceLeft;

    return vec2(sampleX*balanceLeft,sampleX*balanceRight);
}

vec2 piano(float time,float note)
{
    return .5*(pianoSingle(time, note)
              +pianoSingle(time+0.005, note+12.));
}

vec2 mainSound( in int samp,float time)
{
    float tr= 0.0;
    float verse = floor(time/4.0);
    if (mod(verse,4.) >=2.)
      tr = 5.0;

