vec3 hsv(float h,float s,float v) {
	return mix(vec3(1.),clamp((abs(fract(h+vec3(3.,2.,1.)/3.)*6.-3.)-1.),0.,1.),s)*v;
}
vec2 cmul(vec2 a, vec2 b) {
    return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

vec2 cdiv(vec2 a, vec2 b) {
    return vec2(a.x*b.x + a.y*b.y, a.y*b.x - a.x*b.y) / dot(b, b);
}

vec2 mobius(vec2 z, vec2 a, vec2 b, vec2 c, vec2 d) {
    return cdiv(cmul(a, z) + b, cmul(c, z) + d);
}

float interp() {
    float tt = abs(iTime*2.0);
    float n1 = 2.0/max(floor(tt/2.0+1.0), 1.0);
    float n2 = 2.0/max(floor(tt/2.0+2.0), 1.0);
    float v = smoothstep(0.0, 1.5, mod(tt, 2.0));
	return mix(n1, n2, v);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float i = interp();

	float pi = 3.14159;
	float a = i*pi;
	vec2 p = -1.0+2.0*fragCoord.xy/iResolution.xy;
    p.x *= iResolution.x/iResolution.y;
	float s = sin(a);
	float c = cos(a);
	p /= dot(p, p);
	p /= 8.0;
	vec3 col = vec3(0);
	for (int i = 0; i < 150; i++) {
		if (dot(p, p) > 4.0) {
			col = hsv(iTime*0.1+0.5, 1.0, 1.0);
			break;
		}
		p = cmul(p, p) + p;
		p = vec2(-s*p.y+c*p.x, s*p.x+c*p.y);
		col += hsv(iTime*0.1+dot(p,p)*4.0, 1.0, 0.01);
	}

	fragColor = vec4( sin(col), 1.0 );

}

vec2 mainSound( in int samp,float time)
{
    float n = floor(time*4.0);
    float s = sin(time)*0.125+0.125;

    return vec2( 4.0*asin(sin(6.2831*(330.0*mod(n*(n-1.0)*0.5, 64.0)/32.0)*time)))
                *vec2(smoothstep(0.25-s, s, mod(time, 0.25)),
                smoothstep(s, 0.25-s, mod(time+s, 0.25)));
}
