#define time iTime

float wat(vec2 uv) {
    return fract(dot(uv.xy, vec2(1.323, 1.343)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv *= 1.3;
    uv.x *= iResolution.x / iResolution.y;
    float w = sin(uv.x * 20.);
    float z = cos(uv.y * 20.);
    float c = max(z, w);

    if (c < .2){
        c = wat(uv + time);
        c = mix(c, w, .3);
    }

    vec3 col = vec3(0.4, 0.7, 1.);
	fragColor = vec4(col * c, 1.0);
}
