vec2 rotate( in vec2 p, in float t )
{
	return p * cos( -t ) + vec2( p.y, -p.x ) * sin( -t );
}   

vec3 rotate( in vec3 p, in vec3 t )
{
    p.yz = rotate( p.yz, t.x );
    p.zx = rotate( p.zx, t.y );
	p.xy = rotate( p.xy, t.z );
    return p;
}

float map( in vec3 p )
{
    return length( p ) - 2.5; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = ( 2.0 * fragCoord.xy - iResolution.xy ) / iResolution.y;
    vec3 rd =normalize( vec3( p, -1.8 ) );
	vec3 ro = vec3( 0.0, 0.0, 3.5 );
    vec3 rot = vec3( iTime * 0.25, iTime * 0.05 , 0.0 );
	ro = rotate( ro, rot );
	rd = rotate( rd, rot );
    float s = 0.1;
    ro /= s;
  	vec3 grid = floor( ro );
	vec3 grid_step = sign( rd );
    vec3 delta = ( grid + 0.5 * ( grid_step + 1.0 ) - ro ) / rd;    
	vec3 delta_step = abs( 1.0 / rd );
    vec3 mask;
    for ( int i = 0; i < 32; i++ )
    {
		if ( map( ( grid + 0.5 ) * s ) < 0.0 ) break;
		vec3 c = step( delta, delta.yzx );
		mask = ( 1.0 - c.zxy ) * c;
		grid += grid_step * mask;	
		delta += delta_step * mask;
	}      
    grid += 0.5 * ( 1.0 - grid_step );
    vec3 uvw = rd * dot( rd, ro ) / dot( mask, grid );    
	//vec3 uvw = ro + rd * dot( grid - ro, mask ) / dot( rd, mask );
    uvw = fract( uvw );
    vec2 uv = vec2( dot( uvw.yzx, mask ), dot( uvw.zxy, mask ) );
    vec3 col = texture( iChannel0, uv ).rgb;
	fragColor = vec4( col, 1.0 );
}
