void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 R = iResolution.xy, 
	uv = (2.*fragCoord.xy - R)/R.y;

    vec2 pos = uv;
    float dep = 0.;
    float c = 3.141592/2.;
    float a = 0.;
    vec2 dir = vec2(1,0);
    for(int i=0;i<15;i++){
        if(length(pos) < 1.0){
        	dep += 0.08;
        }
        a /= 2.;
        if(dot(dir,pos)<0.){
	        pos += dir/2.;
            a += 1.;
        }else{
            pos -= dir/2.;
        }
        dir = vec2(cos(a*c),sin(a*c));
        pos *= 2.;
    }

	fragColor = vec4(vec3(1.-dep),1.0);
}
