#define res_ iResolution

#define d(uv,s) (2. * uv.xy - res_.xy) / res_.y * s


void mainImage(out vec4 o, vec2 f) {
	vec2 p = d(f, 2.);

    // Triangle in one line?
    float d = max(abs(p.x)+p.y, abs(p.y)) - 1.;
    //float d = max(abs(p.x+p.y)+p.y, abs(p.y)) - 1.;
    //float d = max(abs(p.x*1.5)+p.y, abs(p.y)) - 1.;

    // Trapezoid
    //float d = max(abs(p.x)+.3*p.y, abs(p.y)) - 1.;

    vec3 c = vec3(.0, .0, .0);
	c = mix(c, vec3(1.), smoothstep(.02, .01, abs(d)));
    c = mix(c, vec3(1., 0., 0.), fract(-d) / sqrt(d*9.));

    o = vec4(c, 1.);
}
