vec3 o,d,p;

mat3 rz(float a){float s=sin(a),c=cos(a);return mat3(c,s,0.,-s,c,0.,0.,0.,1.);}

struct M{float d;vec3 c;};

M m=M(1./0.,vec3(0.));

void minm(M b){if (b.d<m.d)m=b;}

vec3 hsv(vec3 c){
    vec4 k=vec4(1.,2./3.,1./3.,3.);
    vec3 p=abs(fract(c.xxx+k.xyz)*6.-k.www);
    return c.z*mix(k.xxx,clamp(p-k.xxx,0.,1.),c.y);
}

void map(vec3 p)
{
    p*=rz(sin(iTime+p.z*1.)*.3);
    vec3 c=hsv(vec3(dot(floor(p),vec3(.5,.2,.1)),1.,1.));
    m=M(length(mod(p,1.)-0.5)-.3,c);
}

float df()
{
    int i;
    float rd=0.;
    for (i=0;i<30;++i)
    {
        p=o+rd*d;
        map(p);
        rd+=m.d;
        if (m.d<.01) break;
    }
    return 1.-float(i)/30.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy-.5;
    uv.x*=iResolution.x/iResolution.y;
    o=vec3(uv,1.);
    d=vec3(uv,-1.);
    float md=df();
    vec3 c=md*m.c;
    c=pow(c,vec3(1./2.2));
	fragColor = vec4(c,1.0);
}
