void mainImage( out vec4 O, vec2 U )
{
    if (max(U.x,U.y)/iResolution.y<.2) {              // bottom-left snippet
        O = texture(iChannel0,U/iResolution.y/.2);
        if (max(U.x,U.y)+.5>=floor(.2*iResolution.y)) O=vec4(1,0,0,0);
        return; 
    }
    vec2 M = iMouse.xy; if (length(M)<10.) M = vec2(iResolution.x); // cyclicity scale

    float N = M.x, s=0., 
          m = texelFetch(iChannel0,ivec2(0,U.y), 0).w, // mean for the line on [0,N[
          v = texelFetch(iChannel0,ivec2(0,U.y), 0).z; // variancen for the line on [0,N[

    for( float x = 0.; x < N; x++)
        s +=   ( texelFetch(iChannel0,ivec2(mod(x+N/2.,N),U.y), 0).x - m )
             * ( texelFetch(iChannel0,ivec2(mod(x+U.x ,N),U.y), 0).x - m );

    s /= N;                                            // covariance
    s /= v;                                            // correlation
    s = sqrt(s);                                       // sqrt( correlation ) 

    O = vec4(sqrt(s));                                 // gamma correction for visual display
}

float hash( uint n ) { // from https://www.shadertoy.com/view/llGSzw  Base: Hugo Elias. ToFloat: https://iquilezles.org/articles/sfrand
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return uintBitsToFloat( (n>>9U) | 0x3f800000U ) - 1.;
}

vec4 noise(vec2 U) {
    U-=.5; 
    return vec4( hash(uint(U.x+iResolution.x*U.y)) );  // white noise
}

vec4 bluenoise(vec2 U) {                               // blue noise
#define V(i,j)  noise(U+vec2(i,j))
  //U=floor(U/8.); 
    vec4 N = 8./9.* noise( U ) 
           - 1./9.*( V(-1,-1)+V(0,-1)+V(1,-1) +V(-1,0)+V(1,0) +V(-1,1)+V(0,1)+V(1,1) );  
    return N*2. + .5;   // or *1 to avoid saturation at the price of low contrast
}

vec4 image(vec2 U, sampler2D T) {
    return texture(T, U/iResolution.xy);                // image
}

void mainImage( out vec4 O, vec2 U )
{
    float t = mod(floor(iTime),5.);

    O =   t==0. ? noise(U)                       // white noise
        : t==1. ? bluenoise(U)                   // blue noise
        : t==2. ? image(U, iChannel0)            // image
        : t==3. ? image(U, iChannel1)            // image
        :         image(U, iChannel2);           // image
}

// copy A in B, store line average in B[0,y].w and variance in B[0,y].z

void mainImage( out vec4 O, vec2 U )
{
    O = texelFetch(iChannel0, ivec2(U), 0); // copy A

    if (U.x==.5) {
    	vec2 M = iMouse.xy; if (length(M)<10.) M = vec2(iResolution.x); // cyclicity scale
    	float N = M.x, t,m1=0.,m2=0.;

        for( float x = 0.; x < N; x++) {
            t = texelFetch(iChannel0,ivec2(x,U.y), 0).x;
        	m1 += t;           // 1st moment
            m2 += t*t;         // 2nd moment
        }

        m1 /= N; m2 /= N;
        O.w = m1;              // mean on [0,N[
        O.z = (m2-m1*m1);      // variance on [0,N[
    }
}
