void mainImage( out vec4 f, vec2 g )
{
	f = texelFetch(iChannel0, ivec2(g),0);
}

void mainImage( out vec4 f, vec2 g )
{
    f.xyz = iResolution;
    vec2 v = (g+g-f.xy)/f.y, r;
    f *= texture(iChannel0, g/f.xy) / length(f);
    r.x = iTime;
    r.y = length(v) * 5. + sin(r.x*.5) * 9.;
    r.x += atan(v.x, v.y) * 5., 
    f += .001/dot(
        r = fract(r)-.5,
        r);
}
