// Prints character in the range uv = (0,0) to (.5,1)
float PrintCharacter( in int char, in vec2 uv )
{
    uint idx = uint(char);
    vec2 charp = vec2( idx&0xFU, 0xFU-(idx>>4U) );

    if ( min(uv.x,uv.y) < .0 || max(uv.x-.5,uv.y-1.) > .0 )
        return 0.;
    uv.x += .25;

//    return step(textureLod(iChannel0, (uv+charp)/16., .0).w,.5);
//    return smoothstep(.53,.47,textureLod(iChannel1, (uv+charp)/16., .0).w );
//    return textureLod(iChannel0, (uv+charp)/16., .0).x;
    return 1.-textureLod(iChannel1, (uv+charp)/16., .0).w;
}

void mainImage( out vec4 o, in vec2 u )
{
    u = ((u-iResolution.xy*.5)/iResolution.y)*150.;

    float t = 0.;

    const int string[] = int[]( 0x54, 0x68, 0x69, 0x73, 0x20, 0x69, 0x73, 0x20, 0x6a, 0x75, 0x73, 0x74, 0x20, 0x61, 0x20, 0x71, 0x75, 0x69, 0x63, 0x6b, 0x20, 0x74, 0x65, 0x73, 0x74 );
    vec2 uv = u / 14. - vec2(-float(string.length())*.25,.2);

    float a = .0;
    for ( int i=0; i < string.length(); i++ )
    {
        a += PrintCharacter( string[i], uv ); uv.x -= .5;
    }
    t += smoothstep(.487,.513,a);

    const int string2[] = int[]( 0x74, 0x6f, 0x20, 0x73, 0x65, 0x65, 0x20, 0x69, 0x66, 0x20, 0x49, 0x20, 0x63, 0x61, 0x6e, 0x20, 0x77, 0x72, 0x69, 0x74, 0x65 );
    uv = vec2(u) / 10. - vec2(-float(string2.length())*.25,-.9);
    a = .0;
    for ( int i=0; i < string2.length(); i++ )
    {
        a += PrintCharacter( string2[i], uv ); uv.x -= .5;
    }
    t += smoothstep(.48,.52,a);

    const int string3[] = int[]( 0x6c, 0x6f, 0x6e, 0x67, 0x20, 0x73, 0x65, 0x6e, 0x74, 0x65, 0x6e, 0x63, 0x65, 0x73, 0x20, 0x69, 0x6e, 0x20, 0x73, 0x68, 0x61, 0x64, 0x65, 0x72, 0x73, 0x21, 0x20, 0x3a, 0x29 );
    uv = vec2(u) / 10. - vec2(-float(string3.length())*.25,-2.);
    a = .0;
    for ( int i=0; i < string3.length(); i++ )
    {
        a += PrintCharacter( string3[i], uv ); uv.x -= .5;
    }
    t += smoothstep(.48,.52,a);

    o = mix( o, 1.-o, t );
}
