#define SHOW_POINTS 1
#define SHOW_SEGMENTS 1
#define SHOW_DUAL_POINTS 1
#define SHOW_DUAL 1

// segment.x is distance to closest point
// segment.y is barycentric coefficient for closest point
// segment.z is length of closest point on curve, on the curve, starting from A
// segment.a is approximate length of curve
vec4 segment( vec2 p, vec2 a, vec2 b )
{
  a -= p;
  b -= p;
  vec3 k = vec3( dot(a,a) , dot(b,b) , dot(a,b) );
  float t = (k.x - k.z)/( k.x + k.y - 2.*k.z );
  float len = length(b-a);

  if( t < 0. ){
      return vec4( sqrt(k.x) , 0. , 0. , len );
  } else if( t > 1. ){
      return vec4( sqrt(k.y) , 1. , len , len );
  } else {
  	return vec4( length(a*(1.-t) + b*t) , t , t*len , len );
  }
}

// https://www.shadertoy.com/view/4djSRW
#define ITERATIONS 4


// *** Change these to suit your range of random numbers..

// *** Use this for integer stepped ranges, ie Value-Noise/Perlin noise functions.
#define HASHSCALE1 .1031
#define HASHSCALE3 vec3(.1031, .1030, .0973)
#define HASHSCALE4 vec4(1031, .1030, .0973, .1099)
//----------------------------------------------------------------------------------------
///  3 out, 2 in...
vec3 hash32(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * HASHSCALE3);
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

vec3 hash3point(vec2 p)
{
    //vec3 col = hash32(p);
    vec3 col = 
            hash32(p*1.25672+vec2(.2,.8))
          * hash32(vec2(p.y,p.x)/3.42464-vec2(.5,.0))
          - hash32(vec2(3.0+p.y,1.2))
    ;

    return pow(
        (abs(col)+max(col,0.0))/2.0
        , vec3(.6,.5,.4)
    );
}

float coeffDistPoint(vec2 uv,vec2 colPoint,float scale)
{    
    //float dist = length(uv - colPoint) * scale;
    //dist = pow(dist,0.25);
    //dist = 1.0 - smoothstep(0.0,1.0,dist);

    vec2 uv_ = (uv - colPoint)*scale*24.0;
    float dist = dot(uv_,uv_);
    return  1.0 / ( 1.0 + dist );
}

void mixColorPoint(vec2 uv,inout vec3 col,vec2 colPoint,float scale)
{
    col = mix(
        col , 
        hash3point(colPoint) ,
        coeffDistPoint(uv,colPoint,scale)
    );
}


vec3 mixColorLine(vec2 uv,vec3 currentCol,vec3 colLine,vec2 lineA,vec2 lineB,float scale)
{
    return mix(
        currentCol , 
        colLine ,
        1.0 - smoothstep(0.0,1.0,sqrt(sqrt( segment(uv,lineA,lineB).x * scale )))
    );
}

bool pointsOnSameSideOfLine(vec2 pointA,vec2 pointB,vec2 lineA, vec2 lineB)
{
    vec2 n = lineB - lineA;
    n = vec2(n.y,-n.x);
    return  dot(pointA-lineA,n)
          * dot(pointB-lineA,n)
    > 0.0;
}


float viewportMagnify = 1.0;
vec2 screenToViewport(vec2 uv)
{
    return (uv - iResolution.xy/2.0 ) / min(iResolution.x,iResolution.y) * viewportMagnify;
}

vec2 viewportToScreen(vec2 uv,vec2 base)
{
    return (uv - base/4.0) / viewportMagnify * min(iResolution.x,iResolution.y) +  iResolution.xy/2.0;
    //return (uv - iResolution.xy/2.0 ) / min(iResolution.x,iResolution.y) * viewportMagnify;
}

// there is three kind of points
// in kisrhombille
// named here A,B,C
struct Equerre
{
    vec2 A; // Right angle  => 4 connections
    vec2 B; // Acute angle  => 12 connections
    vec2 C; // Obtuse angle => 6 connections

    vec2 D; // on AB
    vec2 E; // on BC
};

// when decomposing an A,B,C triangle into thre subtriangles
// A & B stays respectively A & B points
// C becomes a B point
// D created is a C point
// E created is an A point

float det22(vec2 a,vec2 b)
{
    return a.x*b.y - a.y*b.x;
}

vec3 barycentricCoordinate(vec2 P,Equerre T)
{
    vec2 PA = P - T.A;
    vec2 PB = P - T.B;
    vec2 PC = P - T.C;

    vec3 r = vec3(
        det22(PB,PC),
        det22(PC,PA),
        det22(PA,PB)
    );

    return r / (r.x + r.y + r.z);
}

#define EQUERRE_COPY(T,Q) \
    T.A = Q.A; \
    T.B = Q.B; \
    T.C = Q.C;

#define EQUERRE_COMPUTE_DE(T) \
	T.D = (2.0 * T.A + T.B)/3.0; \
	T.E = (T.B + T.C)/2.0;

#define EQUERRE_GET1(T,Q) \
	T.A = Q.A; \
    T.B = Q.C; \
    T.C = Q.D;

#define EQUERRE_GET2(T,Q) \
	T.A = Q.E; \
    T.B = Q.B; \
    T.C = Q.D;

#define EQUERRE_GET3(T,Q) \
	T.A = Q.E; \
    T.B = Q.C; \
    T.C = Q.D;


#define EQUERRE_GET_NEIGHBOUR_AB(T,Q) \
	T.A = Q.A; \
    T.B = Q.B; \
    T.C = 2.0 * Q.A - Q.C;

#define EQUERRE_GET_NEIGHBOUR_AC(T,Q) \
	T.A = Q.A; \
    T.B = 2.0 * Q.A - Q.B; \
    T.C = Q.C;

#define EQUERRE_GET_NEIGHBOUR_BC(T,Q) \
	T.A = (3.0 * Q.C + Q.B)/2.0 - Q.A; \
    T.B = Q.B; \
    T.C = Q.C;

#define EQUERRE_COND1(X,T) \
	pointsOnSameSideOfLine(uv,T.A,T.D,T.C)

#define EQUERRE_COND2(X,T) \
	pointsOnSameSideOfLine(uv,T.B,T.D,T.E)

#define EQUERRE_CENTER(T) ((T.A+T.B+T.C)/3.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(1.0);

    int nbIterations = 3 + int(floor((sin(iTime*3.14/9.0)+1.1)*2.5));

	int EquerreVersion = 1;
    vec2 base = vec2(sqrt(3.0),1.0);
    base *= 50000.0;


	vec2 uv = screenToViewport(fragCoord.xy );

    viewportMagnify = base.x / 4.0;
    uv *= viewportMagnify;
    uv += base/4.0;

    // Base Triangle
    Equerre Tri;
    Equerre Tri_TMP;
    Tri.A = Tri.B = Tri.C = vec2(0.0);
    Tri.B.x += base.x;
    Tri.C.y += base.y;

    for(int i = 0 ; i < nbIterations ; i++)
    {
        EQUERRE_COMPUTE_DE(Tri);

        if( EQUERRE_COND1(uv,Tri) )
        {
            EQUERRE_GET1(Tri_TMP,Tri);
        }
        else if( EQUERRE_COND2(uv,Tri) )
        {
            EQUERRE_GET2(Tri_TMP,Tri);
        }
        else 
        {
            EQUERRE_GET3(Tri_TMP,Tri);
        }

        fragColor.rgb = 1.15 - fragColor.gbr;
        EQUERRE_COPY(Tri,Tri_TMP);
    }

    fragColor.rgb = vec3(1.0);

    float scale = float(nbIterations);
    scale = pow(2.0,scale)/viewportMagnify/scale;

    vec3 EquerreCoeffs = barycentricCoordinate(uv,Tri);
    vec3 EquerreColor =
          EquerreCoeffs.x * hash3point(Tri.A)
        + EquerreCoeffs.y * hash3point(Tri.B)
        + EquerreCoeffs.z * hash3point(Tri.C)
    ;//vec3(1.0,0.0,0.0);

    vec3 EquerreDualColor = vec3(0.0,0.0,0.0);

    #if SHOW_SEGMENTS==1
        #define OPERATION1(x,y) fragColor.rgb = mixColorLine(uv,fragColor.rgb,EquerreColor,x,y,scale);
    	OPERATION1(Tri.A,Tri.B);
    	OPERATION1(Tri.B,Tri.C);
    	OPERATION1(Tri.C,Tri.A);
    #endif

    scale /= sqrt(float(nbIterations));

    #if SHOW_POINTS==1
    	#define OPERATION2(x) fragColor.rgb = mix( EquerreColor , fragColor.rgb , 1.0 - coeffDistPoint(uv,x,scale))

        OPERATION2(Tri.A);
        OPERATION2(Tri.B);
        OPERATION2(Tri.C);

    #endif

    float k = iTime/1.0*3.14;
    vec2 mouse = iMouse.z > .5 ?
          iMouse.xy / iResolution.xy
        : vec2(1.0/3.0) + 0.2 * vec2(cos(k),sin(k));
    vec3 coeffs;
    coeffs.xy = mouse.xy;
    coeffs.z = 1.0 - mouse.x - mouse.y;
    if( coeffs.z < 0.0 )
    {
        coeffs.x = 2.0;
        coeffs.y = 1.0;
        coeffs.z = sqrt(3.0);
        coeffs /= coeffs.x + coeffs.y + coeffs.z;
    }

    #define EQUERRE_CENTER_MIX(T) (T.A * coeffs.x + T.B * coeffs.y + T.C * coeffs.z)
    vec2 TriCenterMix = EQUERRE_CENTER_MIX(Tri);

    #if SHOW_DUAL_POINTS==1
        fragColor.rgb = mix( EquerreDualColor , fragColor.rgb , 1.0 - coeffDistPoint(uv,TriCenterMix,scale));
    #endif
    scale *= 6.0;

    #if SHOW_DUAL==1
    	Equerre Neighbour;
        #define OPERATION3(x,y) fragColor.rgb = mixColorLine(uv,fragColor.rgb,EquerreDualColor,x,y,scale);

    	EQUERRE_GET_NEIGHBOUR_AB(Neighbour,Tri);
    	OPERATION3(EQUERRE_CENTER_MIX(Neighbour),TriCenterMix);

    	EQUERRE_GET_NEIGHBOUR_BC(Neighbour,Tri);
    	OPERATION3(EQUERRE_CENTER_MIX(Neighbour),TriCenterMix);

    	EQUERRE_GET_NEIGHBOUR_AC(Neighbour,Tri);
    	OPERATION3(EQUERRE_CENTER_MIX(Neighbour),TriCenterMix);

    #endif

    fragColor.rgb = tanh(fragColor.rgb*2.5);
}
