#define P(A) vec2(cos(A - iTime * s) * r, sin(A - iTime * s) * r)

void mainImage(out vec4 o, vec2 d)
{
	d  = (d - .5 * (o.xy = iResolution.xy)) / o.y;
    o -= o;

    float r, s = .1, a = s;

    for(; a < 6.3; a += s)                                                         // Iterate angle around circle
        for(r = s; r < 1.; r += s)                                                 // Iterate circle radius
        {    
            vec2 p = d - P(a),                                                     // One point on the infinite line
                 b = P(a + s) - P(a);                                              // Another point on the line

            o += 
                smoothstep(.003, -.004, length(p - b * dot(p, b) / dot(b, b))) *   // Draw the tangent line
                (1. - length(d));                                                  // Darken as we move away from the center
        }
}
