// Press Play! It 'grows' the figure with the iTime variable

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // Change these :)
    float n = 14.0;
    float d = 3.0; 

    const float multiplier = 140.0;
    const float speed = 3.5;
    const float growSpeed = 0.0; // Change this to 0.0 to see the rose pattern only

    // Don't change these :)
    float width = multiplier / 30.0;
    float r;
    float k = n / d;

    fragCoord.xy -= iResolution.xy / 2.0;
    fragColor.xyz = vec3(0.4);

    for(float i = 0.0; i < d * n * 2.0; i++) {
        float a = atan(fragCoord.y, fragCoord.x) - (i - d * 2.0) * 3.14;
        if(iTime * speed >= abs(a) * 3.14) {
            r = cos(k * a) * multiplier + iTime * growSpeed;
            vec2 p = vec2(cos(a), sin(a)) * r + fragCoord;
            if(length(p) < width) {
                fragColor = vec4(abs(fragCoord.xy) / 255.0, abs(a) / 255.0, 1.0);

            }
        }
    }
}
