float map(vec3 p){
    //vec3 q = fract(p) * 2.0 - 1.0;
    float r = 4.0 * (sin(iTime) + 1.4);
    vec3 q = mod(p,r)-0.5*r;
    vec3 c = vec3(0.0, 0.0, 0.05);

	return min(min(min(length(q) - (1.1 + sin(iTime + length(p * 0.4) * 2.0)) * 0.25, length(q.xz-c.xy)-c.z), length(q.xy-c.xy)-c.z), length(q.yz-c.xy)-c.z);
}

float trace(vec3 o, vec3 r){
	float t = 0.0;
    for(int i = 0; i < 30; ++i){
        vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.8;
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float fov = 30.0 * (sin(iTime*0.5)+1.02);

    uv = uv * fov - fov / 2.0;

    uv.x *= iResolution.x / iResolution.y;

    vec3 r = normalize(vec3(uv,  1.0));

    float the =  0.2 * iTime;
    //r.yz *= mat2(cos(the), -sin(the), sin(the), cos(the));
    r.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));

    vec3 o = vec3(0.0, 0.0, (1.0 + sin(iTime)) * 3.0); //Camera

    float t = trace(o, r);

    float fog = (3.0 * sin(iTime * 2.0) + 4.0) * 1.0 / (1.0 + t * t * 0.1);

    if(fog < 0.3)fog *= 2.0 * (1.5 + sin(iTime * 0.2));

    vec3 fc = vec3(fog);

	fragColor = vec4(fog * uv.x, fog, fog * uv.y, 1.0);
}
