void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // uvs
    vec2 uv0 = fragCoord.xy / iResolution.xy;
	vec2 uv1 = vec2(1.0 - uv0.x, uv0.y);
	vec2 uv2 = vec2(uv0.x, 1.0 - uv0.y);
	vec2 uv3 = vec2(1.0 - uv0.x, 1.0 - uv0.y);

    // texture
	vec4 tex0 = texture(iChannel0, uv0.xy);
	vec4 tex1 = texture(iChannel0, uv1.xy);
	vec4 tex2 = texture(iChannel0, uv2.xy);
	vec4 tex3 = texture(iChannel0, uv3.xy);

	vec4 texcolor = (uv0.x >= 0.5 && uv0.y >= 0.5) ? tex0 :
				    (uv0.x <= 0.5 && uv0.y >= 0.5) ? tex1 :
				    (uv0.x >= 0.5 && uv0.y <= 0.5) ? tex2 :
				                                     tex3;

    // color
	vec4 overlay = max(tex0, tex1);
	overlay = max(overlay, tex2);
	overlay = max(overlay, tex3);

	vec3 color = (texcolor.rgb + overlay.rgb) * 0.5;

    fragColor = vec4(color.rgb, 1.0);

}
