void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    fragColor = atan(.1*texture(iChannel0,uv)*iResolution.x/(iMouse.x+10.))*2./acos(-1.);
}

#define DOTS 10
#define ITERATIONS 4000
vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec2 getPolar(vec2 a){
    a.x*=acos(-1.);
 	return sqrt(a.y)*vec2(cos(a.x),sin(a.x));   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        vec2 uv = 2.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    uv.y = abs(uv.y);
    vec3 color = vec3(0),col=color,col2 = col;
    float eps = 3./iResolution.y;
    bool escape;
    if(length(uv)<2.){
    for(int j=0;j<DOTS;j++){	
        escape = false;
        vec2 z =2.* getPolar(hash21(iDate.w+float(j)))
                 ,c=z,z2=z;
        for(int i=0;i++<ITERATIONS;z=z*mat2(z.x,-z.y,z.yx)+c){
            float interpolant = clamp(distance(z,z2),0.,1.);
            z2=z;
            if(length(z)>2.){
             	escape = true;
                i=ITERATIONS;
            }
            if(distance(z,uv)<eps){
                    col  = (1.0 - interpolant) * vec3(0.1, 0.4, 1.0) + interpolant * vec3(1.0, 0.6, 0.1);//color for buddabrot
                    col2 = (1.0 - interpolant) * vec3(0.0, 1.0, 0.0) + interpolant * vec3(1.0, 0.1, 0.9);//color for anti-buddabrot
            }  
        }
        color += escape?col:col2*0.;
        col*=0.;col2*=0.;
        //replace col2*0 with col2*0.1 or something to also show the anti-buddabrot
    }
    }
    fragColor = texture(iChannel0,fragCoord/iResolution.xy)+vec4(color,1.0);
}
