// N-Ary Sub-Division Pattern - written 2015 by Jakob Thomsen

void mainImage( out vec4 o, vec2 U )
{
    o -= o;
    U /= iResolution.xy;
    //float n = 2.;
    float n = mod(ceil(iTime), 9.) + 2.;
    //o += floor(n * fract(exp2(ceil(-log2(U.y))) * U.x / 2.)) / (n - 1.);
    o += floor(n * fract(exp2(ceil(-log2(U.y))) * U.x / 2.)) / --n; // Optimization by FabriceNeyret2
}
