void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= vec2(0.5, 0.5);
    float r = length(uv);
    float theta = atan(uv.y, uv.x) + 3.14159;
    fragColor = r <= 0.5 && r >= 0.4 && theta < mod(iTime, 6.28318) ? vec4(1, 1, 1, 1) : vec4(0,0,0,0);
}
