const float minRe = -2.5;
const float maxRe = 1.0;
const float minIm = -1.2;
const float maxIm = 1.2;
const int maxIterations = 30;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    float re_factor = (maxRe - minRe)/(iResolution.x - 1.0);
    float im_factor = (maxIm - minIm)/(iResolution.y - 1.0);
    vec2 c = vec2(minRe + fragCoord.x * re_factor, maxIm - fragCoord.y * im_factor);    
    vec2 z = c;
    fragColor = vec4(0, 0, 0, 1);
    for(int n = 0; n < maxIterations; ++n)
    {
        z = mat2(z,-z.y,z.x) * z + c;
        if(length(z) > 2.0 )
        {
            fragColor = vec4(float(n) / float(maxIterations), 0, 0, 1);
            break;
        }             
    }
}
