void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 channel0 = texture(iChannel0, uv);
    vec4 channel1 = texture(iChannel1, uv);
    vec4 channel2 = texture(iChannel2, uv);
    vec4 channel3 = texture(iChannel3, uv);

    vec4 outputPixel = channel0;
    outputPixel.rgb *= channel3.rgb;
    outputPixel.rgb += channel1.rgb;
    outputPixel.rgb *= channel2.rgb;
    outputPixel.rgb += channel2.rgb;
    outputPixel.rgb *= channel1.rgb;
    outputPixel.rgb += channel3.rgb;
    outputPixel.rgb *= channel0.rgb;

    int modulo = int(mod(iTime * 16.0 * smoothstep(0.0, iResolution.y, iMouse.y), 4.0));
    if(modulo == 0)
        outputPixel.rgb += channel0.rgb;
    else if(modulo == 1)
        outputPixel.rgb += channel1.rgb;
    else if(modulo == 2)
        outputPixel.rgb += channel2.rgb;
    else if(modulo == 3)
        outputPixel.rgb += channel3.rgb;

	fragColor = outputPixel;
}
