// psychedelic version of https://www.shadertoy.com/view/XsKXzG

void mainImage( out vec4 O,  vec2 U )
{
    U = sin(U*20./iResolution.y); 

    O = .5+.5*sin( 3.14* (U.x+U.y+iDate.w) + vec4(0,2.1,-2.1,0)); 
    // without .5+.5* : only R/G/B
    // without 3.14*  : time-varying colors

    O /= max(O.x,max(O.y,O.z)); // saturates colors
}
