//TRIANGLE WAVE
float tri(float x) {
    return abs(2.0 * fract(x) - 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;    
    float rx = tri(length(uv) + iTime * 0.6) ;
    fragColor = vec4(vec3(rx),1.0);
}
