void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 q = fragCoord.xy / iResolution.xy;
    fragColor = textureLod(iChannel0, q, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 vUv = fragCoord.xy / iResolution.xy;
    vec3 uv =    texture(iChannel0, vUv).xyz;
        if(iFrame<10) {
        fragColor = texture(iChannel1, vUv);
    } else {
        fragColor = textureLod(iChannel0, q, 2.0);
    }

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy/iResolution.xy;
	vec2 center = iMouse.xy/iResolution.xy;
    vec2 previous = iMouse.w/iResolution.xy;
    vec2 sub = previous - center;
    float smoothstep_5;
    if (iMouse.z > 0.) {
    vec2 sub1 = center - uv;
    float length_4 = length(sub1);
     smoothstep_5 = smoothstep(0.1, 0.01, length_4);
         }
    vec2 mul_6 = sub * smoothstep_5;

    fragColor = vec4(texture( iChannel0, uv + mul_6).rgb, 2.);  
}
