#define rep 0.8 // try smaller numbers around 0.2. looks cool

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
    float mx = fragCoord.x / iMouse.y;

    vec3 pixel = vec3(0.2, 0.2, 0.2); 

    vec2 h = fract(uv / length(rep * uv * uv) + iTime);

    h -= 0.5;

    //pixel = mix(pixel, vec3(0.0, 1.0, 0.0), step(min(h.x, h.y) / log(length(uv * 5.5 * sin(iTime * 0.25))), 0.01));
     pixel = mix(pixel, vec3(0.0, 1.0, 0.0), step(min(h.x, h.y) , 0.01));

    float shade = smoothstep(0.68, 0.0, h.x); // lines shadows
   	float s = smoothstep(-0.05, 0.3, length(uv * uv) * 0.5); // far dark shadow
    vec3 s2 =  mix(vec3(1.0, 0.0, 1.0), vec3(.3, 1.0, 1.3), 0.2); // additional coloring

    fragColor = vec4(mix(pixel, s2, 0.25) * shade * s * 1.2, 1.0);

}
