void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  // normalise the output pixel coord into 0-1
  vec2 norm = fragCoord.xy / iResolution.xy;

  // normalise mouse pos 0.1
  vec2 mouse = iMouse.xy / iResolution.xy;  

  // get that bit (~pixel) of the texture
  vec2 pix = vec2(norm.x, mouse.y);

  // output as is
  fragColor = texture(iChannel0, pix);
}
