void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale=sin(iTime/3.0)+2.0;
    float offset=scale/2.0-0.5;
    //Inputs
    vec2 scaledCoor=vec2(fragCoord/iResolution.xy*scale)-vec2(offset);

    vec4 inputTexture=texture( iChannel0, scaledCoor);
    vec4 paddingColor=vec4(0.0);


    if(scale>1.0){
        float test=1.0;
    }
    vec4 paddedOutput;

    vec2 xy=fragCoord.xy/iResolution.st; //We obtain our coordinates for the current pixel

    if(xy.x<(0.5+1.0/scale/2.0) && xy.x>(0.5-1.0/scale/2.0) && xy.y<(0.5+1.0/scale/2.0) && xy.y>(0.5-1.0/scale/2.0)){
        paddedOutput=inputTexture;
    }else{
        paddedOutput=paddingColor;
    }

    fragColor=paddedOutput;
}
