#define SC(a) vec2(sin(a),cos(a))
void mainImage( out vec4 O, in vec2 fr ){

    vec2 r = iResolution.xy;
    vec2 uv = fr / r.y - .5;
    float angle = iTime + sin(iTime) + atan(uv.y, uv.x);

    float w = length(uv * 1.5);
   float f =cos(iTime+sin(w*1.5) * 16.) * .04;

    uv += SC( angle ) * cos(angle * 3.0) * f;
    float m = length(uv*2.5);

    O += sin( 1.5 + cos( m * 10.) * 20.) * 3.;

}
