float AB_SCALE=0.95;
float factor=0.25; //mixval, default 0.25
float stepA=0.1; //0.2;
float stepB=0.9; //2.0;

vec2 displace(vec2 uv, vec2 offset)
{   
  //float d = smoothstep(0.2,2.0,texture(iChannel1, (uv*1.0 - vec2(iTime /8.0,0)) + offset).r) * 0.25;
    float d = smoothstep(stepA,stepB,texture(iChannel0,uv + offset).r) * factor;

    return vec2(d);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord/iResolution.xy;

    vec2 dr = displace(uv, vec2(0   , 0.02) * AB_SCALE),
         dg = displace(uv, vec2(0.01, 0.01) * AB_SCALE),
         db = displace(uv, vec2(0.01, 0   ) * AB_SCALE);

    vec3 color = vec3(0);
    color += vec3(1, 0, 0)*texture(iChannel0, uv - dr).r;
    color += vec3(0, 1, 0)*texture(iChannel0, uv - dg).g;
    color += vec3(0, 0, 1)*texture(iChannel0, uv - db).b;

    fragColor = vec4(color, 1.0);
}
