vec4 texelGet(sampler2D tex, vec2 size, vec2 coord) {
    return texture(tex, vec2(coord.x / size.x,
                               coord.y / size.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float thresh = 0.33;
    vec4 greenScreen = normalize(vec4(13, 163, 38, 255));
	vec4 texel = texelGet(iChannel0, iResolution.xy, fragCoord.xy);

    if (distance(greenScreen, texel) < thresh)
    {
        vec4 backgroundTexel = texelGet(iChannel1, iResolution.xy, fragCoord.xy);
        fragColor = backgroundTexel;
    }
    else
    {
    	fragColor = texel;   
    }
}
