#define A 0.005
float f(vec2 u, vec2 a, vec2 b) {
	return smoothstep(-A,A,dot(normalize(u-a),normalize((a-b).yx*vec2(-1,1))));
}

float circle(vec2 u, vec2 p, float r) {
	return 1.-smoothstep(r,r+A,distance(u,p));
}

void mainImage( out vec4 O, in vec2 U )
{
    vec2 R = iResolution.xy;
	U = (U - R* .5) / R.y;
    float T0 = iTime * 0.5;
    float T1 = iTime * 0.123;
    vec2 p0 = vec2(cos(T0),sin(T0))*0.25, p1 = vec2(cos(T1),sin(T1))*0.25;
    float v = f(U, p1, p0);
    float g = max(circle(U,p0,0.01),circle(U,p1,0.01)); 
	O = vec4(v,g,0.0,1.0);
}
