const float speed = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //main
	vec2 p_m = fragCoord.xy / iResolution.xy;

    //distortion
    vec2 p_d = p_m;

    //just mirror it for fun :D so you can see the effect
    if(p_m.y < 0.5)
    {
        p_m.y =  1. - p_m.y;
    }

    //on bottom section do the main distortion stuff
    if((fragCoord.xy / iResolution.xy).y < 0.5)
    {
        //move the distortion
    	p_d.t -= iTime * speed;

        //dst = distortion in this case
    	vec4 dst_map_val = texture(iChannel1, p_d);

        //from 0 to 1  -> -1 to 1
    	vec2 dst_offset = dst_map_val.xy;
    	dst_offset -= vec2(.5,.5);
    	dst_offset *= 2.;

    	dst_offset *= 0.01;

    	vec2 dist_tex_coord = p_m.st + dst_offset;
    	fragColor = texture(iChannel0, dist_tex_coord); 

    }
    //upper section for fun :D
    else
    {
    	fragColor = texture(iChannel0, fragCoord.xy / iResolution.xy); 
    }
}
