void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel1, uv);

    vec4 jcvd = texture(iChannel0,uv-vec2(.01,-.02));
    fragColor.xyz *= 1. - (.5 * jcvd.a);

   	jcvd = texture(iChannel0, uv);
    fragColor.xyz = mix(fragColor.xyz, jcvd.xyz, jcvd.a);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);

    vec3 gs = vec3(0., 1., 0.);
    vec3 df = fragColor.xyz - gs.xyz;

    fragColor.a = smoothstep(.475, .65, dot(df,df));

}
