#define T(x) mat2(  cos(x+vec4(0,-pi,pi,0)/4.) )
#define N(v,m,t)  ( round ( (v)*(m)+t ) - (t) ) / (m)
#define R iResolution
float pi = 6.283;
void mainImage( out vec4 O, in vec2 I )
{
    float r = -iTime*8.;
    vec2 p = (I+I-R.xy)/R.y*T(r);


    float n = length(p),
     t = N( atan(p.y,p.x) +r , 80./pi ,0. ) -r, 
     d =  N( n, 14.+n*1.6, t/pi);

    O = vec4(1,0,.8,0) * 
        (1.-(
            (1.2*(pow(dot(pow(abs(
                (p-d*cos(t-vec2(0,pi/4.)))
                *T(t+pi/4.)*vec2(d,.6)*T(pi/8.+pi/2e2)
            ),vec2(1.2)),vec2(1)),1./1.2))
            -.02*n)*R.y/4.
            ));
}
