// variant of https://shadertoy.com/view/XsVBz3

#define S(v,r)  smoothstep ( 3./R.y, 0., length(v)-r ) // antialiased draw

#define hue(v)  ( .6 + .6 * cos( 6.3*(v) + vec4(0,23,21,0) ) ) // from https://www.shadertoy.com/view/ll2cDc

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    U = ( U+U - R ) / R.y;                           // normalized coordinates

    float r = .5,                                    // circle radius
          n = .1 + 3.*(.5+.5*sin(iTime)),            // dots density
          l = length(U),                             // polar coordinates
          a = atan(U.y,U.x);
    l *= 4.;                                         // spiral zoom
    r = l - a/6.283;                                 // ceil() = spire number
    a += 6.283 * (ceil(r)-.5);                       // extended polar angle
    a = a*a/2.;                                      // param along spiral : int 0..a { r(a).da }

    float u = ( fract( a*n/6.283 ) - .5 ) / n,       // local coordinates in
          v = fract(r) - .5,                         // cells along the circle
          i = floor( a*n/6.283 );                    // dot number

    O = S( vec2(u,v)/4., .03 ) * hue(i/8.);          // draw blobs

  //O = vec4( fract(r), fract(.15*a), 0,0);          // draw parameterization
}
