uniform sampler2D source_image;

vec3 texel_radius;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 TexCoord = fragCoord / iResolution.xy;
    texel_radius = vec3(
        1.0 / iResolution.x, 
        1.0 / iResolution.y, 
        20.0
    );
    fragColor = blur(source_image, TexCoord, vec2(texel_radius.x, 0.0));
}

#define RADIUS 150.0
#define INV_SQRT_2PI_X3 1.1968268412042980338198381798031
vec4 blur(sampler2D sampler, vec2 uv, vec2 texel){
    float r = RADIUS;

    float exp_value = -4.5 / r / r;
    float sqrt_value = INV_SQRT_2PI_X3 / r;

    float sum = 0.0;
    vec4 value = vec4(0.0);

    float x = 1.0;
    while (x <= r)
    {
        float currentScale = exp(exp_value * x * x);
        sum += currentScale;

        vec2 dudv = texel * x;
        value += currentScale * (
            texture(sampler, uv - dudv) + 
            texture(sampler, uv + dudv)
            );
        x += 1.0;
    }


    float correction = 1.0 / sqrt_value - 2.0 * sum;
    value += texture(sampler, uv) * correction;   
    return value * sqrt_value;
}
