//the original one was https://www.shadertoy.com/view/4sVBRz. 
//make color different with the original one
float ring(vec2 st) {
  float r = 0.4;
  float dr = 0.15;

  float d = length(st);
  float c = smoothstep(r, r - (dr/2.0), d) + smoothstep(r, r + (dr/2.0), d);

  return c;
}

//make the ring colorful
float convertColor(float v) {
  return v * v; 
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord / iResolution.xy - 0.5;
  uv.x *= iResolution.x / iResolution.y;

  fragColor = vec4(1.0 - ring(uv), 1.0 - convertColor(ring(uv)), 1.0 - convertColor(ring(uv)), 1.0);
}
