void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 col = texture(iChannel0, uv).xyz;

	float mult = 1000.0;
	float step = 300.0;

	col.x = floor((col.x * mult) / step) * step / mult;
	col.y = floor((col.y * mult) / step) * step / mult;
	col.z = floor((col.z * mult) / step) * step / mult;

	fragColor = vec4(col, 1.0);
}
