#define pi 3.141592654

vec2 hash22(vec2 p)//Dave Hoskins https://www.shadertoy.com/view/4djSRW
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec3 hash32(vec2 p)//Dave Hoskins https://www.shadertoy.com/view/4djSRW
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

float chebyshevMetric(vec2 a, vec2 b){return max(abs(a.x - b.x), abs(a.y - b.y));}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor -= fragColor;
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv *= 10.0;
    uv += 2.0;
    float d0 = 9e9;
    vec2 p0;
    for(int i = -1; i < 2; i++){
        for(int j = -1; j < 2; j++){
            vec2 p = floor(uv) + vec2(i, j);
            vec2 h = hash22(p);
            vec2 g = p + .5 + .5 * sin(iTime * h);
            float d = chebyshevMetric(uv, g);
            if(d < d0){d0 = d; p0 = p;}
        }
    }
    fragColor.xyz = hash32(p0);
    fragColor.xyz /= fragColor.x + fragColor.y + fragColor.z;
    fragColor.xyz = pow(fragColor.xyz, vec3(2.0 * d0));
}
