void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0 * fragCoord.xy / iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    vec2 mouseuv = 2.0 * iMouse.xy / iResolution.xy - 1.0;
    mouseuv.x *= iResolution.x/iResolution.y;

    //vec3 ray = vec3(sin(iTime) - cos(iTime), sin(iTime), 1.0);
    vec3 ray = vec3(mouseuv.xy, 1.0);

    float radius = abs(sin(iTime) / 5.0) + 0.1;
    float x = (uv.x + (sin(iTime)));
    float y = uv.y;
    float z = sqrt(radius*radius - x*x - y*y);

    float circleEq = x*x + y*y + z*z;

    if(abs(circleEq - pow(radius, 2.0)) < 0.01)
    {
        float p = dot(ray, vec3(x,y,z));
        fragColor = vec4(p, p, p, 1.0);
    }
    else
    {
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }
}
