//#define animateIterations
//#define zoom
#define animateColor

float maxIterationsC = 100.;
float minIterationsC = 10.;

vec2 offset = vec2(-30., 0.);

struct Coordinates
{
    float real;
    float imagenary;
};

struct Frac_Return_Val
{
	float iterations;
    float real;
    float imaginary;
};

Frac_Return_Val MandelbrotFunc(float maxIterations, Coordinates coordinates)
{
    float real = 0.;
    float imaginary = 0.;

    float iterations = 0.;    

   	for (float i = 0.; i < maxIterations; i++)
    {
        iterations++;

        if (pow(real, 2.) + pow(imaginary, 2.) > 4.) 
        {
            Frac_Return_Val r_Val;
    		r_Val.iterations = iterations;
    		r_Val.real = real;
    		r_Val.imaginary = imaginary;

            return r_Val;
        }

        float temp = real;
        real = pow(real, 2.) - pow(imaginary, 2.) + coordinates.real;
        imaginary = 2. * temp * imaginary + coordinates.imagenary;
    } 

    Frac_Return_Val r_Val;
    r_Val.iterations = iterations;
    r_Val.real = real;
    r_Val.imaginary = imaginary;

    return r_Val;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #ifdef animateIterations
    float maxIterations = maxIterationsC * abs(sin(iTime)) + minIterationsC;    
    #else
    float maxIterations = maxIterationsC;
    #endif

    Coordinates coordinates;

    #ifdef constantCoordinate
    coordinates.real = 1. / 100.;
    coordinates.imagenary = 1. / 100.;
    #else
    coordinates.real = (fragCoord.x - iResolution.x / 2. + offset.x) / iResolution.y * 3.;
    coordinates.imagenary = (fragCoord.y - iResolution.y / 2. + offset.y) / iResolution.y * 3.;
    #endif

    #ifdef zoom
    coordinates.real /= (sin(iTime * 0.1) * 1000.);
    coordinates.imagenary /= (sin(iTime * 0.1) * 1000.);
    #endif

    Frac_Return_Val r_Val = MandelbrotFunc(maxIterations, coordinates);

    vec4 color;

    if (r_Val.iterations >= maxIterations) {
        color = vec4(abs(r_Val.real) * 1.5, abs(r_Val.imaginary) * 1.5, 1, 1.);
        //color = vec4(0,0,0,0);
    }
    else {
    	color = vec4(r_Val.iterations / 64., r_Val.iterations / 128., r_Val.iterations / 32., 1.);
        //color = vec4(0,0,0,0);
    }



    #ifdef animateColor
    float st = abs(sin(iTime)) * .4 + 1.;
    color *= vec4(1. * st, 1. * st , 1. * st , 1.);
    #endif
    fragColor = color;
}

float Sin01 (float time)
{
    return  sin(time * 0.0) * 3.;
}

vec2 mainSound( in int samp, float time )
{
    vec2 sound = vec2(Sin01(time), Sin01(time));    

    return sound;
}
