/// This code might be outdated, please make sure CURRENT_YEAR is up to date 

#define CURRENT_YEAR 2019.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float nonConstantZeroToPreventCompilerOptimization = (CURRENT_YEAR - iDate.r);
	float f = 100.0;
	f += 0.5 + nonConstantZeroToPreventCompilerOptimization;
	float x = fragCoord.x;

	if (x == f){
		x /= iResolution.x;
		f /= iResolution.x;
		float d = abs(sign(x - f));
		fragColor = vec4(d);
		}
	else
		fragColor = vec4(0.0);
}
