vec3 light = vec3(0.0);

void addLight(vec2 pos, vec3 lightColor, float radius, float glow, vec2 fragCoord)
{
	vec3 color;
	float distance = length(fragCoord.xy-pos.xy);
	if(distance < radius-glow)color = max(distance/radius, 1.0)*lightColor;
	else color = (glow/abs(distance-radius))*lightColor;
	light = max(light, color);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pos = vec2(sin(iTime)*min(iResolution.x, iResolution.y)/2.0+iResolution.x/2.0, cos(iTime)*min(iResolution.x, iResolution.y)/2.0+iResolution.y/2.0);
	addLight(pos, vec3(1.0, 0.0, 0.0), 100.0, 1.0, fragCoord);
	addLight(pos, vec3(0.0, 1.0, 0.0), 100.0, 10.0, fragCoord);
	addLight(pos, vec3(0.0, 0.0, 1.0), 100.0, 100.0, fragCoord);
 	vec4 tex1 = texture(iChannel0, vec2(fragCoord.x/iResolution.x, 
										  fragCoord.y/iResolution.y));
 	vec3 outColor = 
		light.rgb*tex1.rgb;
 	fragColor = vec4(outColor ,1.0);
}

vec2 mainSound( in int samp,float time)
{
    return vec2(0.0);
