#define duration 300
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;
	float aspect = iResolution.x / iResolution.y;
    float zoom = 2.0;
    vec2 q = vec2(0.0, 0.0);
    p = 2.0 * p - 1.0;
	p.x *= aspect;
    p /= zoom;
	vec4 c = vec4(0.0);
	int step = int(mod(iTime * 60.0, float(duration)));
	vec2 ms = 2.0 * iMouse.xy / iResolution.xy - 1.0;
	ms.x *= aspect;
    ms /= zoom;
	vec2 ball = ms;
	int count = 0;
	vec2 z = p;
	for (int count = 0; count < duration; count++)
	{
		if (distance(ball, p) < 0.05)
		{
            // trail code from https://www.shadertoy.com/view/MsBGzy
			c += 0.05 * vec4(1.0 - distance(ball, p) / 0.5);
		}
		if (count > step)
		{
			continue;
		}
		z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + ms;

		ball = z;
	}
	if (distance(ball, p) < 0.02)
	{
		c = vec4(0.0, 1.0, 0.0, 1.0);
	}
	fragColor = c;
}
