float m_modf(float i, float j) {
    return i - j * floor(i / j);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 metaballs[30];

    for(int i = 0; i < 30; i++) {
        metaballs[i] = vec2(float(i) / 30.0 * (iResolution.x / iResolution.y),
                            m_modf((sin(iTime * abs(float(i < 15 ? i : 15 - (i - 15))) / 5.0) + 1.0) / 2.0, 1.0));
    }

	vec2 ssnormal = fragCoord.xy / iResolution.y;

    float frag = 0.0;

    for(int i = 0; i < 30; i++)
        frag += 0.014 / distance(metaballs[i].xy, ssnormal);

    frag = clamp(frag, 0.0, 1.0);
    frag = frag > 0.6 ? frag : 0.0;
    frag = frag < 1.0 && frag != 0.0 ? (frag - 0.6) * (1.0 / 0.6) : frag;

    vec3 fragCol = vec3(frag, frag, frag) * vec3(100.0 / 255.0, 149.0 / 255.0, 237.0 / 255.0);

    fragColor = vec4(fragCol.xyz, 1.0);
}
