float roundBox(vec2 coord, vec2 pos, vec2 b, float c ){
  return 1.-floor(length(max(abs(coord-pos)-b,c)));
}
float circle(vec2 coord, vec2 pos, float size){
    return min(floor(distance(coord,pos)-size),0.);
}
float sdCapsule( vec2 p, vec2 a, vec2 b, float r ){
    vec2 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return min(floor(length( pa - ba*h ) - r),0.);
}
mat2 rotate(float Angle){
    mat2 rotation = mat2(
        vec2( cos(Angle),  sin(Angle)),
        vec2(-sin(Angle),  cos(Angle))
    );
	return rotation;
}

float triangle( vec2 coord, vec2 pos, float angle, float thick, float size){
    vec2 original_coord = coord;
    coord += pos;
    coord *= rotate(angle);
    float collision = 0.0;
    collision += sdCapsule( coord, vec2( 0.0, 0.333)*size, vec2( 0.3,-0.2)*size, thick );
    collision += sdCapsule( coord, vec2( 0.0, 0.333)*size, vec2(-0.3,-0.2)*size, thick );
    collision += sdCapsule( coord, vec2( 0.3,-0.2)*size, vec2(-0.3,-0.2)*size, thick );
    collision += 3.0*texture( iChannel0, original_coord * 0.6 + vec2(iTime*0.02) ).b * 0.6;
    collision += 2.5*texture( iChannel0, original_coord * 0.1 + vec2(iTime*0.001) ).b * 0.4;
    return -min(max(-collision,0.0),1.0);
}
vec3 overlay(){
	vec2 p = gl_FragCoord.xy / iResolution.xy;

	float aspectCorrection = (iResolution.x/iResolution.y);
	vec2 coordinate_entered = 2.0 * p - 1.0;
	vec2 coord = vec2(aspectCorrection,1.0) *coordinate_entered;

    vec3 COLOR = vec3(0.0);
    float collision = 0.0;
    for( float i = 0.0; i < 14.0; i++){
        vec2 triangle_position = vec2(-mod(iTime+i*0.2+i*0.55,4.0)+2.0,sin(iTime+i));
        triangle_position.y /= triangle_position.x + 2.3;
        collision += triangle( coord, triangle_position, iTime+i, 0.03, 0.9 + sin(i*5235.1112313+iTime) * 0.2);
    }
    ;
    if(collision<0.0){
        COLOR = vec3(0.5);
    }
    return COLOR;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 p = fragCoord.xy / iResolution.xy;

	float aspectCorrection = (iResolution.x/iResolution.y);
	vec2 coordinate_entered = 2.0 * p - 1.0;
	vec2 coord = vec2(aspectCorrection,1.0) *coordinate_entered;

	float vignette = 1.0 / max(0.25 + 0.3*dot(coord,coord),1.);
    coord.x += 0.6;
    coord /= 1.6+sin(iTime*2.0)*0.1;
    coord *= rotate(0.2 + 0.1*sin(iTime));
	vec3 COLOR =(vec3(0.9,0.5,0.4)
        		+vec3(floor(mod(coord.y*2.,1.)*3.+.015))
        		*vec3(1.0,0.4,0.9) );


    vec2 position = vec2(0.);
    vec2 size = vec2(11.,0.3);
    if(coord.x<0.){
    size.y += (.631*sin(coord.x*4.)+1.)*texture(iChannel1,.4*coord+vec2(iTime*.17,0.)).r*1.;
    size.y += (.341*sin(coord.x*2.)+1.)*texture(iChannel1,.4*coord+vec2(iTime*.127,0.)).r*.3;
    size.y /= -.5+pow(-coord.x*.017-1.2,3.)+.1*sin(iTime+-coord.x*11.);
    } else {
    size.y += (.631*sin(coord.x*4.)+1.)*texture(iChannel1,.4*coord+vec2(-iTime*.17,0.)).r*1.;
    size.y += (.341*sin(coord.x*2.)+1.)*texture(iChannel1,.4*coord+vec2(-iTime*.127,0.)).r*.3;
    size.y /= -.5+pow(-coord.x*.017-1.2,3.)+.1*sin(iTime+-coord.x*11.);

    }
    float corner = 0.1;
    COLOR -= max(roundBox(coord*5., position, size, corner )*2.,0.);
    COLOR += overlay();
    float head_size = sin(iTime)*.06+mod(iTime*2.,1.)*.03;
    COLOR += 4.*circle(coord, vec2(0.), .5+head_size);
    for(float i=0.; i<3.141*2.; i+=3.141*.25){
    	COLOR += 4.*sdCapsule(coord, vec2(0.0), vec2(cos(i+iTime),sin(i+iTime))*.7, 0.1);
    }


    if(length(coord*vec2(.6,1.-sin(coord.x*5.-3.141*.5)))-.1<.15)
    COLOR = vec3(1.);
    if(length(coord)-.1<.1)
    COLOR = vec3(0.);
    if(length(coord)-.1<.01)
    COLOR = vec3(1.);


	fragColor = vec4( COLOR*vignette
         				,1.0);
}
