const int mi = 60;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 z = (fragCoord.xy-(iResolution/2.0).xy)/400.0, c = (iMouse.xy/iResolution.xy - vec2(0.5, 0.5))*3.0;
    if(iMouse.xy == vec2(0, 0)) z = vec2(0, 20);
    float ozx;
    for(int iters = 0; iters < mi; iters++){
        ozx = z.x;
        z.x = z.x*z.x-z.y*z.y + c.x;
        z.y = ozx*z.y*2.0 + c.y;
        if(length(z) > 2.0){
            fragColor = vec4(0.0, 0.0, 0.0, 1.0);
            return;
        }
    }
    float n = sin(length(z))/2.0+0.5;
    fragColor = vec4(n, n, n, 1.0);
}
