void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float freq = 8.0;
    float m = .5;

    float oo = step(.5,mod(uv[1]*freq*.5,1.))*.5;

    float x = mod(oo+uv[0]*freq,m)*2.;
    float y = mod(uv[1]*freq,m)*2.;

    float ex = step(.5,mod(oo+uv[0]*freq,1.));
    float ey = step(.5,mod(uv[1]*freq,1.));

    float xx = mix(x,1.-x,ex)*2.;
    float yy = mix(y,1.-y,ey);
    float d = min(xx,yy);
    vec4 c = vec4(d);
    //c = vec4(smoothstep(.1,.2,d));
	fragColor = c;

}
