void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = cos(iTime);
    float y = sin(iTime);
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    p.x += x;
    p.y += y;
	float radius = sqrt(p.x * p.x + p.y * p.y); 
	fragColor = vec4(radius*0.5, 0.3*radius, 1.0-radius, 1.0);
}
